SET @code = 'bhvet';

SELECT code, website_id, store_id FROM core_store WHERE code = @code;

-- All customers Integravet
SELECT
	cev.value AS ID_ERP,
	ce.email AS EMAIL,
	cev1.value AS NOME,
	cev2.value AS RazaoSocial,
	ce.is_active AS STATUS,
	ce.mp_cc_is_approved AS Aprovados,
	cev3.value AS ORIGEM
FROM
	customer_entity AS ce
INNER JOIN core_store AS cs ON
	cs.website_id = ce.website_id
LEFT JOIN customer_entity_varchar AS cev ON 
	cev.entity_id = ce.entity_id
AND cev.attribute_id = 183
LEFT JOIN customer_entity_varchar AS cev1 ON 
	cev1.entity_id = ce.entity_id
AND cev1.attribute_id = 5
LEFT JOIN customer_entity_varchar AS cev2 ON 
	cev2.entity_id = ce.entity_id
AND cev2.attribute_id = 137
LEFT JOIN customer_entity_varchar AS cev3 ON 
	cev3.entity_id = ce.entity_id
AND cev3.attribute_id = 186
WHERE
	cs.code = @code;



